// Break up long tasks
function processInChunks() {
  const chunkSize = 100;
  let index = 0;

  function processChunk() {
    const chunk = data.slice(index, index + chunkSize);
    // Process chunk...
    index += chunkSize;

    if (index < data.length) {
      setTimeout(processChunk, 0);
    }
  }

  processChunk();
}
