// Dynamic meta tags for SPAs
function updateMetaTags(route) {
  const meta = getMetaForRoute(route);

  // Update title
  document.title = meta.title;

  // Update meta description
  let metaDesc = document.querySelector('meta[name="description"]');
  if (!metaDesc) {
    metaDesc = document.createElement('meta');
    metaDesc.name = 'description';
    document.head.appendChild(metaDesc);
  }
  metaDesc.content = meta.description;

  // Update canonical URL
  let canonical = document.querySelector('link[rel="canonical"]');
  if (!canonical) {
    canonical = document.createElement('link');
    canonical.rel = 'canonical';
    document.head.appendChild(canonical);
  }
  canonical.href = meta.canonicalUrl;

  // Update structured data
  updateStructuredData(meta.structuredData);
}

// PushState for clean URLs
window.history.pushState({}, meta.title, meta.canonicalUrl);
